sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

  var a=g_form.getReference('u_requested_for');

	if(a!=' '){
		g_form.setValue("u_string_10",5);
		g_form.setValue("u_requested_type",2);

		var DateObj=new Date();
		var mini=DateObj.getMinutes();
		var dat=DateObj.getDate();
		if(mini<10 ){
			mini=0+""+mini;
		}
		if(dat<10){
			dat=0+""+dat;
		}

		var datetime= DateObj.getFullYear()+'-'+(DateObj.getMonth()+1)+'-'+dat; 
  datetime += ' '+DateObj.getHours()+':'+mini+':'+DateObj.getSeconds();
		g_form.setValue("u_when_needed",datetime);
	}else{
		confirm("hello");
	}
}
